/*
    Prototype

        -> JS é uma linguagem baseada em protótipos
        -> __proto__
*/


console.log(true.__proto__);
console.log("Lucas".charAt(2)); // Caracter na posição
console.log("Lucas".endsWith('s')); // Termina com
console.log("Lucas".replace('c', 'k')); // 
console.log("Lucas".length);

let myName = "Lucas Eduardo Ferreira da Rosa";

console.log(myName.slice(0, 13));
